import React, { Component } from 'react';
import getRecipie from '../../../services/fakeData';
import recipieEdit from './RecipieEdit';

class RecipieView extends Component {
    state = { 
        recipie: null
     } 


    componentDidMount = () => {
        this.setState({recipe: getRecipie(1)})
    }


    render() { 
        const {recipie} = this.state
        return (
            <div class="container">
                {/* <h1>Recipie view</h1> */}
                <h1>{recipie.name}</h1>
                <h5>Prep Time: {recipie.prepTime} minutes</h5>
                <h5>Total Time: {recipie.totalTime} minutes</h5>
                <h2>Ingredients</h2>
                <ul>
                    {recipie.ingredients.map(ingredient => (
                        <li>{ingredient.amount} {ingredient.unit} {ingredient.name} {(ingredient.descripiton)}</li>
                    ))}
                </ul>
            </div>
        );
    }
}

export default RecipieView;
