import React, { Component } from 'react';
import getRecipe, { getRecipies } from '../../../services/fakeData';

class RecipeList extends Component {
    state = { 
        // recipies: getRecipies()
        recipies: []
     } 

     async componentDidMount() {
       const {data: recipies} = await Axios.get('/api/recipies/active');
       this.setState({ recipies })
       console.log(this.state)
     }
    render(        
    ) { 

        const {recipies} = this.state 
        return (
            <div class="container">
                        <h1>RecipeList component</h1>


                {console.log(this.state.recipies)}
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Name</th>
                            <th scope="col">Description</th>
                            <th scope="col">Prep Time</th>
                            <th scope="col">Total Time</th>
                            <th scope="col">Servings</th>
                        </tr>
                    </thead>
                    <tbody>
                        {recipies.map(recipie => (
                        <tr key={recipie.id}>
                            <th scope="row">{recipie.id}</th>
                                <td>{recipie.name}</td>
                                <td>{recipie.description}</td>
                                <td>{recipie.prepTime}</td>
                                <td>{recipie.totalTime}</td>
                                <td>{recipie.servings}</td>

                        </tr>
                        ))}

                    </tbody>
                </table>

            </div>



        );
    }
}

export default RecipeList;
