import React, { Component } from 'react';
import getRecipe, { getrecipes } from '../../../services/fakeData';
import Axios from 'axios';

class RecipeList extends Component {
    state = { 
        recipes: []
     } 

    //  async componentDidMount() {
    //    const { data: recipes } = await Axios.get(
    //        '/api/recipes/active'
    //     );
    //   this.setState({ recipes });
    // //    console.log(recipes)
    //    console.log('state: ',this.state)
    //  }

     componentDidMount() {
        Axios.get('/api/recipes/active')
          .then(res => {
            const recipes = res.data;
            this.setState({ recipes });
          })
      }

    render(        
    ) { 

        const {recipes} = this.state 
        return (
            <div class="container">
                        <h1>RecipeList component</h1>


                {/* {console.log(this.state.recipes)} */}
                <table className="table">
                    <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Name</th>
                            <th scope="col">Description</th>
                            <th scope="col">Prep Time</th>
                            <th scope="col">Total Time</th>
                            <th scope="col">Servings</th>
                        </tr>
                    </thead>
                    <tbody>
                        {recipes.map(recipie => (
                        <tr key={recipie.id}>
                            <th scope="row">{recipie.id}</th>
                                <td>{recipie.name}</td>
                                <td>{recipie.description}</td>
                                <td>{recipie.prepTime}</td>
                                <td>{recipie.totalTime}</td>
                                <td>{recipie.servings}</td>

                        </tr>
                        ))}

                    </tbody>
                </table>

            </div>



        );
    }
}

export default RecipeList;
