import React, { Component } from 'react';

const Input = ({ name, label, value, onChange, helpText }) => {
    return (                         
    <div className="mb-3">
        <label htmlFor={name}>{ label }</label>
        <input 
            autoFocus 
            value={value} 
            onChange={onChange}
            type="email" className="form-control" 
            id={name} 
            name={name}
            aria-describedby="emailHelp"
        />
        {helpText ? 
        <div id={`${name}Help`} className="form-text">{ helpText }</div> 
        : '' }

    </div>
 );
}

export default Input;
