import fracty from 'fracty';
import React, { Component } from 'react';
import getRecipe from '../../../services/fakeData';
import recipieEdit from './RecipieEdit';
import { useParams, useRouteMatch } from 'react-router-dom';


class RecipieView extends Component {
    state = { 
        recipe: getRecipe(this.props.match.params.recipeId)
     }


     componentDidMount = () => {
        console.log('component - componentDidMount')
    }

    getCoverPhoto = () => {
        const coverPhotoUrl = this.state.recipe.photos.filter(photo => photo.coverPhoto === true)[0].url
        return coverPhotoUrl
    }




    render() { 
        console.log('component - rendered')
        const {recipe} = this.state
        return (
            <div>
                {/* {console.log('props', this.props)} */}
                <div className="coverPhoto">
                    <img src={this.getCoverPhoto()} alt="" />
                </div>
            <div class="container">
                {/* <h1>Recipe view</h1> */}
                {/* <h1>{recipe.name}</h1> */}
                <div className="container">
                    <div className="row">
                        <h1 className="col-6">{recipe.name}</h1>
                        <div className="col-5"></div>
                        <div className="col-1 self-end justify-content-end">
                            <button type="button" class="btn btn-light">
                                <span class="oi oi-pencil"></span>
                            </button>
                        </div>
                    </div>
                </div>
                <hr />
                <p><strong>Prep Time:</strong> {recipe.prepTime} minutes</p>
                <p><strong>Total Time:</strong> {recipe.totalTime} minutes</p>

                <h2>Ingredients</h2>
                <ul>
                    {recipe.ingredients.map(ingredient => (
                        <li key={ingredient.id}>
                            {fracty(ingredient.amount) + ' '} 
                            {ingredient.unit  + ' '}
                            {ingredient.name} 
                            {ingredient.descripiton ? ' (' + ingredient.descripiton + ')' : ''}</li>
                    ))}
                </ul>
                <h2>Instructions</h2>
                <p>{recipe.description}</p>
                <h3>Steps</h3>
                <ol className="col-md-6">
                    {recipe.instructions.map(instruction => (
                        <li key={instruction.id}>
                            {instruction.step}
                            {instruction.time ? <span> <a href='#'>START TIMER</a></span> : ''}  
                        </li>
                        // <li>
                        //     <div class="custom-control custom-checkbox">
                        //         <input type="checkbox" class="custom-control-input" id="check2">
                        //         <label class="custom-control-label" for="check2">Check me</label>
                        //     </div>
                        // </li>
                    ))}
                </ol>
            </div>
        </div>

        );
    }
}

export default RecipieView;
