import React, { Component } from 'react';

class LoginForm extends Component {
    state = {  } 

    userEmail = React.createRef();
    userPassword = React.createRef();

    handleSubmit = e => {
        e.preventDefault();

        // call the server
        const userEmail = this.userEmail.current.value
        console.log('Submitted - ', userEmail,+ ' ' + userPassword)
    }
    render() { 
        return (
            <div>
                <div className="container">
                    <h1>Login Form</h1>
                    <form onSubmit={this.handleSubmit}>
                        <div className="mb-3">
                            <label htmlFor="InputEmail">Email address</label>
                            <input ref={this.userEmail} type="email" className="form-control" id="InputEmail" aria-describedby="emailHelp"/>
                            <div id="emailHelp" className="form-text">We'll never share your email with anyone else.</div>
                        </div>
                        <div className="mb-3">
                            <label htmlFor="password">Password</label>
                            <input ref={this.userPassword} type="password" className="form-control" id="password"/>
                        </div>
                        <button type="submit" className="btn btn-primary">Submit</button>
                    </form>
                </div>
            </div>
        );
    }
}

export default LoginForm;
